(19, 1, '2021-07-17 17:57:07', '2021-07-17 17:57:07', '<!-- wp:cover {\"url\":\"http://eutamen.gal/wp-content/uploads/2021/07/pexels-pixabay-163431-1024x682.jpg\",\"id\":100,\"align\":\"full\",\"style\":{\"color\":{\"duotone\":[\"#000000\",\"#ffffff\"]}}} -->\n<div class=\"wp-block-cover alignfull has-background-dim\"><img class=\"wp-block-cover__image-background wp-image-100\" alt=\"\" src=\"http://eutamen.gal/wp-content/uploads/2021/07/pexels-pixabay-163431-1024x682.jpg\" data-object-fit=\"cover\"/><div class=\"wp-block-cover__inner-container\"><!-- wp:paragraph {\"fontSize\":\"large\"} -->\n<p class=\"has-large-font-size\">Uso intencional da forza física contra un mesmo, outra persoa, un grupo de persoas ou unha comunidade que ten como consecuencia moi probable danos para a saúde.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:cover -->', 'Violencia física', '', 'publish', 'open', 'closed', '', 'violencia-fisica', '', '', '2021-07-22 08:23:14', '2021-07-22 08:23:14', '', 0, 'http://eutamen.gal/?page_id=19', 0, 'page', '', 4),
